import React, { useState } from 'react'

const EmployeeComponent = () => {

   const [firstName,setFirstName]= useState('');
   const [lastName,setLastName]= useState('');
   const [email,setEmail]= useState('');

   function handleFirstName(event){
        setFirstName(event.target.value);
   }

   function handleLastName(event){
        setLastName(event.target.value);
   }

   function handleEmail(event){
    setEmail(event.target.value);
   }

   function saveEmployee(event){
        event.preventDefault();
        
   }

  return (
    <div className='container'>
        <div className='row'>
            <div className='card'>
                <h3 className='text-center'> Add Employee</h3>
                <div className='card-body'>
                    <form>
                        <div className='form-group mb-2'>
                        <label className='form-label'>First Name</label>
                        <input type='text' placeholder='Enter employee first name' name='firstName' value={firstName} className='form-control' onChange={handleFirstName}></input>
                        </div>
                        <div className='form-group mb-2'>
                        <label className='form-label'>Last Name</label>
                        <input type='text' placeholder='Enter employee last name' name='lastName' value={lastName} className='form-control' onChange={handleLastName}></input>
                        </div>
                        <div className='form-group mb-2'>
                        <label className='form-label'>Email</label>
                        <input type='text' placeholder='Enter employee email' name='email' value={email} className='form-control' onChange={handleEmail}></input>  
                        </div>
                        <button className='btn btn-success' onClick={saveEmployee}>Submit</button>
                    </form>
                </div>
            </div>
        </div>
    </div>
  )
}

export default EmployeeComponent
