import React, { useState } from 'react';
import { createEmployee } from '../services/EmployeeService';
import { useNavigate } from 'react-router-dom';

const EmployeeComponent = () => {

   const [firstName,setFirstName]= useState('');
   const [lastName,setLastName]= useState('');
   const [email,setEmail]= useState('');

   const [errors,setErrors]=useState({
            firstName:'',
            lastName:'',
            email:''
   });

   const navigator=useNavigate();

   function saveEmployee(event){
        event.preventDefault();
        const employee={
            firstName,
            lastName,
            email
        };
        console.log(employee)

        createEmployee(employee).then((response)=>{
            navigator('/employees');
        });    
   }

   function validateForm(){
    let valid=true;
    const errorsCopy={...errors};
    
   }

  return (
    <div className='container'>
        <br></br>
        <div className='row'>
            <div className='card col-sm-6 offset-sm-3'>
                <h3 className='text-center'> Add Employee</h3>
                <div className='card-body'>
                    <form>
                        <div className='form-group mb-2'>
                        <label className='form-label'>First Name</label>
                        <input type='text' placeholder='Enter employee first name' name='firstName' value={firstName} className='form-control' onChange={(event)=>setFirstName(event.target.value)}></input>
                        </div>
                        <div className='form-group mb-2'>
                        <label className='form-label'>Last Name</label>
                        <input type='text' placeholder='Enter employee last name' name='lastName' value={lastName} className='form-control' onChange={(event)=>setLastName(event.target.value)}></input>
                        </div>
                        <div className='form-group mb-2'>
                        <label className='form-label'>Email</label>
                        <input type='text' placeholder='Enter employee email' name='email' value={email} className='form-control' onChange={(event)=>setEmail(event.target.value)}></input>  
                        </div>
                        <button className='btn btn-success' onClick={saveEmployee}>Submit</button>
                    </form>
                </div>
            </div>
        </div>
    </div>
  )
}

export default EmployeeComponent
